mapper.select:
select  ${distinct}

    ${resultColumn}




from Att_Record_Student





        ${order}


        inner join
        (
        SELECT ID as primary_key FROM Att_Record_Student



        ${order}

            LIMIT #{startRow},#{rowSize} 
            LIMIT #{rowSize}

         ) as tmp
         on tmp.primary_key=Att_Record_Student.ID

select 

from Att_Record_Student

     and Att_Record_Student.STUDENT_FK_CODE = #{studentFkCode}
     and Att_Record_Student.SCHOOL_FK_CODE = #{schoolFkCode}
     and Att_Record_Student.TYPE = #{type}
     and Att_Record_Student.FK_CODE = #{fkCode}
     and Att_Record_Student.ATTENDANCE_DAY = #{attendanceDay}
     and Att_Record_Student.UPDATE_TIME = #{updateTime}
     and Att_Record_Student.CREATE_TIME = #{createTime}
     and Att_Record_Student.DEL_STATUS = #{delStatus}
     and Att_Record_Student.ATTENDANCE_STATUS = #{attendanceStatus}
     and Att_Record_Student.CARD_CODE = #{cardCode}
     and Att_Record_Student.ATTENADNCE_TIME = #{attenadnceTime}
     and Att_Record_Student.ID = #{id}

mapper.update:
update Att_Record_Student set
    ATTENADNCE_TIME=#{attenadnceTime}
    ,CARD_CODE=#{cardCode}
    ,ATTENDANCE_STATUS=#{attendanceStatus}
    ,DEL_STATUS=#{delStatus}
    ,CREATE_TIME=#{createTime}
    ,UPDATE_TIME=#{updateTime}
    ,ATTENDANCE_DAY=#{attendanceDay}
    ,FK_CODE=#{fkCode}
    ,TYPE=#{type}
    ,SCHOOL_FK_CODE=#{schoolFkCode}
    ,STUDENT_FK_CODE=#{studentFkCode}
where ID=#{id}
