mapper.select:
select  ${distinct}

    ${resultColumn}




from Att_Setting_Worker





        ${order}


        inner join
        (
        SELECT ID as primary_key FROM Att_Setting_Worker



        ${order}

            LIMIT #{startRow},#{rowSize} 
            LIMIT #{rowSize}

         ) as tmp
         on tmp.primary_key=Att_Setting_Worker.ID

select 

from Att_Setting_Worker

     and Att_Setting_Worker.SIGN_BACK_BIG_TIME = #{signBackBigTime}
     and Att_Setting_Worker.CREATE_TIME = #{createTime}
     and Att_Setting_Worker.SIGN_BIG_TIME = #{signBigTime}
     and Att_Setting_Worker.SIGN_OUT_HOURS_OF_DATE = #{signOutHoursOfDate}
     and Att_Setting_Worker.SIGN_IN_HOURS_OF_DATE = #{signInHoursOfDate}
     and Att_Setting_Worker.DEL_STATUS = #{delStatus}
     and Att_Setting_Worker.EXPIRE_TIME = #{expireTime}
     and Att_Setting_Worker.INITIATE_MODE = #{initiateMode}
     and Att_Setting_Worker.TO_WORK_TIME = #{toWorkTime}
     and Att_Setting_Worker.SCHOOL_FK_CODE = #{schoolFkCode}
     and Att_Setting_Worker.FK_CODE = #{fkCode}
     and Att_Setting_Worker.ATTENDANCE_DAY = #{attendanceDay}
     and Att_Setting_Worker.UPDATE_TIME = #{updateTime}
     and Att_Setting_Worker.ACTIVE_TIME = #{activeTime}
     and Att_Setting_Worker.SIGN_END_TIME = #{signEndTime}
     and Att_Setting_Worker.SIGN_BACK_END_TIME = #{signBackEndTime}
     and Att_Setting_Worker.CAMPUS_FK_CODE = #{campusFkCode}
     and Att_Setting_Worker.OFF_WPRK_TIME = #{offWprkTime}
     and Att_Setting_Worker.ID = #{id}

mapper.update:
update Att_Setting_Worker set
    OFF_WPRK_TIME=#{offWprkTime}
    ,CAMPUS_FK_CODE=#{campusFkCode}
    ,SIGN_BACK_END_TIME=#{signBackEndTime}
    ,SIGN_END_TIME=#{signEndTime}
    ,ACTIVE_TIME=#{activeTime}
    ,UPDATE_TIME=#{updateTime}
    ,ATTENDANCE_DAY=#{attendanceDay}
    ,FK_CODE=#{fkCode}
    ,SCHOOL_FK_CODE=#{schoolFkCode}
    ,TO_WORK_TIME=#{toWorkTime}
    ,INITIATE_MODE=#{initiateMode}
    ,EXPIRE_TIME=#{expireTime}
    ,DEL_STATUS=#{delStatus}
    ,SIGN_IN_HOURS_OF_DATE=#{signInHoursOfDate}
    ,SIGN_OUT_HOURS_OF_DATE=#{signOutHoursOfDate}
    ,SIGN_BIG_TIME=#{signBigTime}
    ,CREATE_TIME=#{createTime}
    ,SIGN_BACK_BIG_TIME=#{signBackBigTime}
where ID=#{id}
